package java_basic;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// while start
		
		int idx = 1, sum = 0;
		 
        while (idx <= 10) {
            // summing up x
            sum = sum + idx;
 
            // Increment the value of x for
            // next iteration
            System.out.println("idx: " + idx);

            idx = idx +1;
        }// close while
        
        System.out.println("Summation: " + sum);
			
		//while end
        
        // while start
		
     		int idx2 = 1;
     		 
             while (idx2 <= 3) {
      
                 System.out.println("image"+idx2+".png");

                 idx2 = idx2 +1;
             }// close while
             
     			
     	//while end
             
             
             
        
        
        
        
		
	}

}
